import React from "react";

const Message = ({ pic, message, time, isUser }) => {
  console.log(message);
  return (
    <div
      className={`flex flex-row w-[95%] mx-auto mt-4 space-x-2 ${
        isUser ? " justify-end" : "items-start"
      }`}
    >
      {/* profile photo */}
      {!isUser && (
        <div className="w-[2.5rem] h-[2.5rem] rounded-full">
          <img src={pic} alt="" className="w-full h-full" />
        </div>
      )}
      {/* message */}
      <div className="max-w-[70%] mt-[0.5rem] flex flex-col space-y-2 text-white text-[0.75rem] leading-4 tracking-[0.0075rem] font-normal">
        {message.map((element) => {
          return (
            <div
              className={`${
                isUser ? "bg-[#FBBC04]" : "bg-[#69235B]"
              } p-2 rounded-b-xl rounded-tr-xl`}
            >
              {element}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Message;
