import React from "react";
import pic from "../assets/Profilepic.png";
// import { BsArrowLeft } from "react-icons/bs";
const Chat = () => {
  return (
    <div className="h-auto w-screen flex flex-col mt-6">
      {/* header */}
      <header className="w-full h-[5rem] bg-white border border-black flex justify-center items-center">
        <div className="w-[90%] md:w-[90%] h-[90%] mx-auto flex flex-row justify-between items-center">
          {/* profile */}
          <div className="flex flex-row w-[60%]">
            {/* icon - back arrow */}
            <div className="w-[1.5rem] h-[1.5rem] sm:w-[2rem] sm:h-[2rem] flex justify-center items-center">
              {/* <BsArrowLeft className="text-[#69235B]" /> */}
            </div>
            {/* profile,name */}
            <div className="flex flex-row space-x-3 sm:space-x-4 justify-center items-center">
              {/* photo */}
              <div className="rounded-full h-[2.75rem] w-[2.75rem] sm:w-[3rem] sm:h-[3rem] relative before:absolute before:bottom-[10%] sm:before:right-[3%] before:right-[5%] before:w-[0.5rem] before:h-[0.5rem] sm:before:w-[0.6rem] sm:before:h-[0.6rem] before:rounded-full before:bg-[#2BEF83] ">
                <img
                  src={pic}
                  alt=""
                  className=" w-full h-full rounded-full "
                />
              </div>

              {/* name */}
              <div className="flex flex-col space-y-1 sm:space-y-2 items-start">
                <h2 className=" font-medium non-italic text-[1rem] sm:text-[1.25rem] leading-[1rem] text-[#69235B]">
                  Arya
                </h2>
                <p className="text-[#797C7B] text-[0.75rem] sm:text-[1rem] font-thin leading-[0.75rem]">
                  Vedic AI Bot
                </p>
              </div>
            </div>
          </div>

          {/* languages */}
          <div className="mr-[1rem]">
            <h2 className="text-[#FBBC04] text-[1rem] sm:text-[1.25rem] font-extrabold non-italic leading-[1rem]">
              A<span className="font-light">/à¤•</span>
            </h2>
          </div>
        </div>
      </header>

      {/* messages */}
      <div></div>
    </div>
  );
};

export default Chat;
