import React from "react";

const Message = ({ pic, message, time, isUser, className }) => {
  console.log(message);
  return (
    <div
      className={`my-8 flex flex-row w-[95%] mx-auto space-x-2 ${
        isUser ? " justify-end" : "justify-start"
      } ${className}`}
    >
      {/* profile photo */}
      {!isUser && (
        <div className="w-[2.5rem] h-[2.5rem] rounded-full">
          <img src={pic} alt="" className="w-full h-full" />
        </div>
      )}
      {/* message */}
      <div className={` max-w-[70%] sm:max-w-[50%] flex flex-col`}>
        <div className="mt-[0.5rem] flex flex-col space-y-1 text-white text-[0.75rem] leading-4 tracking-[0.0075rem] font-normal">
          {message.map((element) => {
            return (
              <div
                className={` ${
                  isUser
                    ? "bg-[#FBBC04] rounded-b-xl rounded-tl-xl"
                    : "bg-[#69235B] rounded-b-xl rounded-tr-xl"
                } p-2 `}
              >
                {element}
              </div>
            );
          })}
        </div>
        <p className=" max-w-[70%] sm:max-w-[50%] mt-1 text-right text-[0.625rem] font-thin leading-[0.625rem] text-[#797C7B] not-italic">
          {time}
        </p>
      </div>
    </div>
  );
};

export default Message;
